import React, { Component } from 'react';
import './App.css';
//import loadScript from 'react-loadscript';

class App extends Component {
  componentDidMount(){
    this.renderMap()
  }
  renderMap = () => {
    <script async defer
    src="https://maps.googleapis.com/maps/api/js?key=YOUR_API_KEY&callback=initMap">
    </script>
    window.initMap = this.initMap
  }
  initMap=()=> {
    //var src="https://maps.googleapis.com/maps/api/js?key=AIzaSyCEulUdUn5mr_K4jX2M1jfM3JT27UiKXpo&callback=initMap"
  
    var infoWindow; 
    infoWindow = new infoWindow.google.maps.InfoWindow();

    var map = new infoWindow.google.maps.Map(document.getElementById('map'), {
      center: {lat: -34.397, lng: 150.644},
      zoom: 6
    });

    var handleLocationError=(browserHasGeolocation, infoWindow, pos) =>{
      
      infoWindow.setPosition(pos);
      infoWindow.setContent(browserHasGeolocation ?
                            'Error: The Geolocation service failed.' :
                            'Error: Your browser doesn\'t support geolocation.');
      infoWindow.open(map);
    }
    
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(function(position) {
        var pos = {
          lat: position.coords.latitude,
          lng: position.coords.longitude
        };

        infoWindow.setPosition(pos);
        infoWindow.setContent('Location found.');
        infoWindow.open(map);
        map.setCenter(pos);
      }, function() {
        handleLocationError(true, infoWindow, map.getCenter());
      });
    } else {
      // Browser doesn't support Geolocation
      handleLocationError(false, infoWindow, map.getCenter());
    }
  }
    
    render() {
    return (
        <main>
        <div id="map"></div>
        </main>
    );
  }
}

