import React, { Component } from 'react';

const loginStyle = {
  width: "90%",
  maxWidth: "315px",
  margin: "20px auto",
  border: "1px solid #ddd",
  borderRadius: "5px",
  padding: "10px"
}

class Login extends Component {
  state = {
    username: '',
    password: ''
  }
  handleChange=(e)=>{
    this.setState({
      [e.target.id]:e.target.value
    })
  }
  handleSubmit=(e)=>{
    //e.preventDefalt();
    console.log(this.state)
  }

  render() {
    return (
      <div style={loginStyle}>
       <h4>SIGN IN</h4>
       Sign In to your account
       <br/>
        <form onSubmit={this.handleSubmit}/>

        <div style={{marginBottom: "10px"}} className="pt-callout pt-icon-info-sign">

          <label className="pt-label">
            Email
          </label>  
          <input style={{width: "100%"}} className="pt-input" name="email" type="email" onChange={this.handleChange}
          placeholder= "Email"/>       

          <label className="pt-label">
            Password
          </label>  
          <input style={{width: "100%"}} className="pt-input" name="password" type="email" onChange={this.handleChange} 
          placeholder= "Password"/>


          <input style={{width: "100%"}} type="submit" className="pt-button pt-intent-primary" value="SignIn"/>
        </div>
      </div>
    );
  }
}

export default Login;


