import React, { Component } from 'react';
import { connect } from 'react-redux';
import {Redirect} from 'react-router-dom';
import {getFirestore} from 'redux-firestore';
import Collapsible from 'react-collapsible';

const loginStyle = {
  width: "50%",
  maxWidth: "1100px",
  margin: "100px auto",
  border: "5px solid #ddd",
  borderRadius: "5px",
  padding: "30px"
}

class Search extends Component {
  
  state = {
    from: '',
    to: '',
    redirectToReferrer: false
  }
  formValidation(){
    console.log("Validated")
  }
  handleChange=(e)=>{
    this.setState({
      [e.target.id] : e.target.value
    })
  }
  handleSubmit=(e)=>{
    e.preventDefault();
    const to= this.state.to;
    const from= this.state.from;
    console.log("From: " + from, "To: " + to);

    const db = getFirestore();
    var trainRef = db.collection("Train");
    var query = trainRef.where(to+".stops", "==" ,"true").where(from+".stops", "==" ,"true")
    
    query.get().then(snapshot=>{
            
      snapshot.docs.forEach(doc=>{

        console.log(doc.data().Details.Name);
        <Collapsible trigger="Start here">
        <p>This is the collapsible content. It can be any element or React component you like.</p>
        <p>It can even be another Collapsible component. Check out the next section!</p>
      </Collapsible>
        // let object = {  title : doc.get('Details.Name'), 
        //                 description :'Departure from '+ from +' : '+ doc.get(from +'.Dep') +'\n'+
        //                    'Arrival at '    + to   +' : '+ doc.get(to+'.Arr')   +'\n'+
        //                    'Description'    +'\n'+                                               
        //                    'Available Classes' ,
        //                 pressed: false
        //     } 
      })
    })
    // quary.get().then(function(doc) {
    //   if (doc.exists) {
    //     console.log("Document data:", doc.data());
    //   } else {
    //     console.log("No such document!");
    //   }
    // }).catch(function(error) {
    // console.log("Error getting document:", error);
    // });
  }
  
  render() {
    const { authError } = this.props;
    const {from} = this.props.location.state || {
      from: {
        pathname: '/Dashboard'
      }
    }
    const {redirectToReferrer} = this.state
    if (redirectToReferrer) {
      return (<Redirect to={from}/>)
    }
    return (
      <div style={loginStyle} className="white">
       
        <form onSubmit={this.handleSubmit}>

        <h5>SHEDULE</h5>
        <div className="input-field">  
        </div>

        <div className="input-field"> 
          <label htmlFor="from">From </label>  
          <input id="from" type="text" ref="from" onChange={this.handleChange}/>
        </div> 
        <div className="input-field"> 
          <label htmlFor="to">To </label>  
          <input id="to" type="text" ref="to" onChange={this.handleChange}/>
        </div> 
        <div className="input-field">
          <button className="btn blue lighten-1 z-depth-0">Search Here</button>  
        </div>
        <div className="red-text center">
            { authError ? <p>{authError}</p>: null}
          </div>
        </form>
      </div>
    );

    
  }
}

const mapStateToProps = (state) => {
  return {
    authError: state.auth.authError
  }
}

// const mapDispatchToProps = (dispatch) => {
//   return{
//     data: (creds) => dispatch(login(creds))
//   }
// }

export default connect(mapStateToProps)(Search);
