import React, { Component } from 'react';
import { connect } from 'react-redux';
import {getFirestore} from 'redux-firestore';
import Collapsible from 'react-collapsible';
import Popup from "reactjs-popup";

const loginStyle = {
  width: "50%",
  maxWidth: "1100px",
  margin: "100px auto",
  border: "5px solid #ddd",
  borderRadius: "5px",
  padding: "30px"
}

class Edit extends Component {
  
  state = {
    train: '',
    redirectToReferrer: false,
    list: [],
    name: '',
    arr: '',
    dep: '',
    clasess: '',
    type: '',
    run: '',
    in: ''
  }

  handleChange=(e)=>{
    this.setState({
      [e.target.id] : e.target.value
    })
  }

  handleUpdate=(e)=>{
    const db = getFirestore();
    var up = db.collection("Train").doc("DC");

    return up.update({
      capital: true
    })
    .then(function() {
      console.log("Document successfully updated!");
    })
    .catch(function(error) {
      console.error("Error updating document: ", error);
    });
  }

  handleDelete=(e)=>{
    this.setState({
      [e.target.id] : e.target.value
    })
  }

  handleSubmit=(e)=>{
    e.preventDefault();
    const train= this.state.train;

    const db = getFirestore();
    var trainRef = db.collection("Train").doc(train);
   
  }
  
  render() {
    const { authError } = this.props;
 
    return (
      <div style={loginStyle} className="white">
       
        <form onSubmit={this.handleSubmit}>

        <h5>Update/ Delete</h5>
        <div className="input-field">  
        </div>

        <div className="input-field"> 
          <label htmlFor="train"> Train </label>  
          <input id="train" type="text" onChange={this.handleChange}/>
        </div> 
       
        <div className="input-field">
          <button className="btn blue lighten-1 z-depth-0">Find Here</button>  
        </div>
        
        <div className="red-text center">
            { authError ? <p>{authError}</p>: null}
          </div>
        </form>
       {items}        
      </div>  
    );
  }
}

const mapStateToProps = (state) => {
  return {
    authError: state.auth.authError
  }
}

export default connect(mapStateToProps)(Edit);
