import React, { Component } from 'react';
//import { Link } from 'react-router-dom';
//import { Button, Card, CardBody, CardGroup, Col, Container, Form, Input, InputGroup, InputGroupAddon, InputGroupText, Row } from 'reactstrap';

class Login extends Component {
  state = {
    username: '',
    password: ''
  }
  handleChange=(e)=>{
    this.setState({
      [e.target.id]:e.target.value
    })
  }
  handleSubmit=(e)=>{
    //e.preventDefalt();
    console.log(this.state)
  }


  render() {
    return (
      <div>
       <h4>SIGN IN</h4>
        <form onSubmit= { (event) => { this.authWithEmailPassword(event) }} ref={ (form) => {this.loginForm = form }}/>

        <div style={{marginBottom: "10px"}} className="pt-callout pt-icon-info-sign">

        <label className="pt-label">
        Email
        <input style={{width: "100%"}} className="pt-input" name="email" type="email" ref={(input) => {this.emailInput = input}} 
        placeholder= "Email">
        </input>  
        </label>
        <label className="pt-label">
        Password
        <input style={{width: "100%"}} className="pt-input" name="password" type="email" ref={(input) => {this.passwordInput = input}} 
        placeholder= "Password">
        </input>  
        </label>
          </div>
         <button style= {{width: "100px"}} className="pt-button pt-intent-primary">
        </button>
      </div>
    );
  }
}

export default Login;


